// parse the tx and verify
func Test_Genesis_Tx(t *testing.T) {

	// mainnet genesis tx
	Genesis_Tx_hex := "" +
		"02" + // version
		"3c" + // unlock time
		"01" + // vin length
		"ff" + // vin #1
		"00" + // height gen input
		"01" + // vout length
		"ffffffffffff07" + // output #1 amount
		"02" + // output 1 type
		"0bf6522f9152fa26cd1fc5c022b1a9e13dab697f3acf4b4d0ca6950a867a1943" + // output #1 key
		"21" + // extra length in bytes
		"01" + // extra pubkey tag
		"1d92826d0656958865a035264725799f39f6988faa97d532f972895de849496d" + // tx pubkey
		"00" // RCT signature none

	tx_data_blob, _ := hex.DecodeString(Genesis_Tx_hex)

	var tx Transaction

	err := tx.DeserializeHeader(tx_data_blob)

	if err != nil {
		t.Errorf("Deserialize mainnet Genesis tx failed\n")
		return
	}

	if fmt.Sprintf("%s", tx.GetHash()) != "5a18d9489bcd353aeaf4a19323d04e90353f98f0d7cc2a030cfd76e19495547d" {
		t.Errorf("Deserialize mainnet Genesis tx  gethash failed\n")
	}

	if !tx.Parse_Extra() {
		t.Errorf("mainnet Genesis tx  extra parsing failed\n")
	}

	expected_key := crypto.HexToKey("1d92826d0656958865a035264725799f39f6988faa97d532f972895de849496d")

	if tx.Extra_map[TX_PUBLIC_KEY] != expected_key {
		t.Errorf("mainnet Genesis tx  extra parsing failed, public key no match\n")
	}

	if tx.IsCoinbase() != true {
		t.Errorf("mainnet Genesis tx  must be Coinbase\n")
	}
}

//
func Test_Transaction_Deserialisation_Height_1302238_second(t *testing.T) {

	// transaction from height 1302238 txid be9d2cf9b473dbbb2c59ffb07b5d812516f94d64121d87ad61956386a4bc3843
	// prefix hash 1bbfda600fa6affc80dae05b1124bf05ed0e20890aa42601441dbe0f6fa81f4b
	tx_data_hex := ""
	tx_data_blob, _ := hex.DecodeString(tx_data_hex)

	var tx Transaction

	err := tx.DeserializeHeader(tx_data_blob)

	if err != nil {
		t.Error("DeserializeHeader  transaction from blockheight 1302238 beb76a82ea17400cd6d7f595f70e1667d2018ed8f5a78d1ce07484222618c3cd\n")
		return
	}

	// verify prefix hash
	calculated_prefix_hash := tx.GetPrefixHash()

	if hex.EncodeToString([]byte(calculated_prefix_hash[:])) != "1bbfda600fa6affc80dae05b1124bf05ed0e20890aa42601441dbe0f6fa81f4b" {
		t.Error("Prefix hash mismatched")
	}

	// be9d2cf9b473dbbb2c59ffb07b5d812516f94d64121d87ad61956386a4bc3843
	calculated_hash := tx.GetHash()

	if hex.EncodeToString([]byte(calculated_hash[:])) != "be9d2cf9b473dbbb2c59ffb07b5d812516f94d64121d87ad61956386a4bc3843" {
		t.Error("transaction hash mismatched")
	}

	// now serialize once again
	//
	serialised := tx.SerializeHeader()
	_ = serialised

	tx.Clear()

	err = tx.DeserializeHeader(serialised)

	/*if err != nil {
		t.Error("DeserializeHeader  transaction from blockheight 1302238 beb76a82ea17400cd6d7f595f70e1667d2018ed8f5a78d1ce07484222618c3cd\n")
		return
	}*/

	if hex.EncodeToString(serialised) != hex.EncodeToString(tx.SerializeHeader()) {

		t.Logf("serialized  %s\n", hex.EncodeToString(serialised))
		t.Logf("reserialized %s\n", hex.EncodeToString(tx.SerializeHeader()))
		t.Error("Serialize TX  Failed")
	}

	if tx.IsCoinbase() == true {
		t.Errorf("be9d2cf9b473dbbb2c59ffb07b5d812516f94d64121d87ad61956386a4bc3843 is NOT Coinbase\n")
	}

}

